import { useState } from "react";
import { motion } from "framer-motion";
import { Menu, X, Home, Settings, User } from "lucide-react";

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="relative">
      {/* دکمه باز و بسته کردن */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="fixed top-4 left-4 z-50 p-2 rounded-lg bg-gray-800 text-white shadow-lg"
      >
        {isOpen ? <X size={24} /> : <Menu size={24} />}
      </button>

      {/* سایدبار */}
      <motion.aside
        initial={{ x: -250 }}
        animate={{ x: isOpen ? 0 : -250 }}
        transition={{ type: "spring", stiffness: 200, damping: 20 }}
        className="fixed top-0 left-0 h-screen w-60 bg-gray-900 text-white shadow-lg flex flex-col items-center p-6"
      >
        <h2 className="text-xl font-bold mb-8">لوگو</h2>
        <nav className="space-y-6 w-full">
          <NavItem icon={<Home size={20} />} text="خانه" />
          <NavItem icon={<User size={20} />} text="پروفایل" />
          <NavItem icon={<Settings size={20} />} text="تنظیمات" />
        </nav>
      </motion.aside>
    </div>
  );
};

// کامپوننت آیتم‌های منو
const NavItem = ({ icon, text }: { icon: React.ReactNode; text: string }) => {
  return (
    <motion.div
      whileHover={{ scale: 1.1 }}
      className="flex items-center space-x-3 w-full p-3 rounded-lg cursor-pointer hover:bg-gray-700 transition"
    >
      {icon}
      <span className="text-lg">{text}</span>
    </motion.div>
  );
};

export default Sidebar;
