import { Home, Settings, User } from "lucide-react";

const Sidebar = () => {
  return (
    <aside className="fixed top-0 right-0 h-screen w-60 bg-gray-900 text-white shadow-lg flex flex-col items-center p-6">
      <h2 className="text-xl font-bold mb-8">لوگو</h2>
      <nav className="space-y-6 w-full">
        <NavItem icon={<Home size={20} />} text="خانه" />
        <NavItem icon={<User size={20} />} text="پروفایل" />
        <NavItem icon={<Settings size={20} />} text="تنظیمات" />
      </nav>
    </aside>
  );
};

const NavItem = ({ icon, text }: { icon: React.ReactNode; text: string }) => {
  return (
    <div className="flex items-center space-x-3 w-full p-3 rounded-lg cursor-pointer hover:bg-gray-700 transition">
      {icon}
      <span className="text-lg">{text}</span>
    </div>
  );
};

export default Sidebar;
