import React, { useState, useEffect } from "react";
import axios from "axios";
import ErrorBox from "../Errorbox/ErrorBox";

export default function Comments() {
  const [comments, setComments] = useState([]);
  const [loading, setLoading] = useState(true);

  // دریافت کامنت‌ها از سرور
  useEffect(() => {
    const fetchComments = async () => {
      try {
        const response = await axios.get("http://localhost:3000/api/comments");
        setComments(response.data);
      } catch (error) {
        console.error("خطا در دریافت کامنت‌ها:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchComments();
  }, []);

  // تابع حذف کامنت
  const handleDeleteComment = async (commentId) => {
    try {
      await axios.delete(`http://localhost:3000/api/comments/${commentId}`);
      setComments(comments.filter((comment) => comment.id !== commentId));
    } catch (error) {
      console.error("خطا در حذف کامنت:", error);
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-xl font-semibold text-gray-800 text-center mb-4">
        لیست کامنت‌ها
      </h2>
      {loading ? (
        <p className="text-center">در حال بارگذاری...</p>
      ) : comments.length === 0 ? (
        <ErrorBox msg="هیچ کامنتی موجود نیست" />
      ) : (
        <ul className="space-y-4">
          {comments.map((comment) => (
            <li key={comment.id} className="p-4 bg-gray-100 rounded-lg shadow">
              <p className="text-gray-700">{comment.body}</p>
              <button
                onClick={() => handleDeleteComment(comment.id)}
                className="mt-2 bg-red-500 text-white py-1 px-3 rounded hover:bg-red-700"
              >
                حذف
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
