import { Routes, Route, Navigate, useRoutes } from "react-router-dom";
import { useEffect, useState } from "react";
import Sidebar from "./Components/SideBar/Sidebar";
import Header from "./Components/Header/Header";
import Login from "./Components/Login/Login";
import routes from "./routes";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [adminData, setAdminData] = useState(null);
  const router = useRoutes(routes);

  useEffect(() => {
    const token = localStorage.getItem("adminToken");
    const adminInfo = localStorage.getItem("adminData");

    console.log("üîç Checking authentication...");
    console.log("Token:", token);
    console.log("Admin Data:", adminInfo);

    if (token && adminInfo) {
      setIsAuthenticated(true);
      setAdminData(JSON.parse(adminInfo));
    }
  }, []);

  return (
    <Routes>
      <Route path="/login" element={<Login onLogin={(admin) => {
        console.log("‚úÖ Admin Logged In:", admin);
        setIsAuthenticated(true);
        setAdminData(admin);
      }} />} />

      <Route
        path="/*"
        element={isAuthenticated ? (
          <div className="flex h-screen">
            <Sidebar />
            <div className="flex flex-col flex-1">
              <Header adminData={adminData} />
              <main className="flex-1 p-6 bg-gray-100 overflow-auto">
                {router || <p>ÿµŸÅÿ≠Ÿá‚Äåÿß€å Ÿæ€åÿØÿß ŸÜÿ¥ÿØ</p>}
              </main>
            </div>
          </div>
        ) : (
          <Navigate to="/login" />
        )}
      />
    </Routes>
  );
}

export default App;
