import { Routes, Route, Navigate, useRoutes, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import Sidebar from "./Components/SideBar/Sidebar";
import Header from "./Components/Header/Header";
import Login from "./Components/login/Login";
import routes from "./routes";
import axios from "axios";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [adminData, setAdminData] = useState(null);
  const router = useRoutes(routes);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("adminToken");

    if (token) {
      axios
        .get("http://localhost:3000/api/admins/me", {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => {
          setIsAuthenticated(true);
          setAdminData(res.data);
        })
        .catch(() => {
          setIsAuthenticated(false);
          localStorage.removeItem("adminToken");
          navigate("/login"); // اگر توکن نامعتبر است، کاربر را به صفحه لاگین هدایت می‌کند
        });
    }
  }, []);

  return (
    <Routes>
      <Route
        path="/login"
        element={<Login onLogin={(admin) => {
          setIsAuthenticated(true);
          setAdminData(admin);
        }} />}
      />
      <Route
        path="/*"
        element={
          isAuthenticated ? (
            <div className="flex h-screen">
              <Sidebar />
              <div className="flex flex-col flex-1">
                <Header adminData={adminData} />
                <main className="flex-1 p-6 bg-gray-100 overflow-auto">
                  {router || <p>صفحه‌ای پیدا نشد</p>}
                </main>
              </div>
            </div>
          ) : (
            <Navigate to="/login" />
          )
        }
      />
    </Routes>
  );
}

export default App;
