import React, { useEffect, useState, useRef } from "react";
import axios from "axios";

export default function Productstable() {
  const [products, setProducts] = useState([]);
  const [editProduct, setEditProduct] = useState(null); // ذخیره محصولی که قرار است ویرایش شود
  const formRef = useRef(null); // برای کنترل فرم ویرایش

  // دریافت لیست محصولات
  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = () => {
    axios
      .get("http://localhost:3000/api/products")
      .then((response) => setProducts(response.data))
      .catch((error) => console.error("Error fetching products:", error));
  };

  // حذف محصول
  const handleDelete = (id) => {
    if (window.confirm("آیا از حذف این محصول مطمئن هستید؟")) {
      axios
        .delete(`http://localhost:3000/api/products/${id}`)
        .then(() => {
          setProducts(products.filter((product) => product.id !== id));
        })
        .catch((error) => console.error("Error deleting product:", error));
    }
  };

  // ویرایش محصول
  const handleEdit = (product) => {
    setEditProduct(product);
    if (formRef.current) {
      formRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  // ارسال ویرایش به API
  const handleUpdate = (e) => {
    e.preventDefault();
    axios
      .put(`http://localhost:3000/api/products/${editProduct.id}`, editProduct)
      .then(() => {
        setEditProduct(null);
        fetchProducts();
      })
      .catch((error) => console.error("Error updating product:", error));
  };

  return (
    <div className="overflow-x-auto w-full">
      <table className="products-table w-full border-collapse bg-white shadow-lg rounded-xl overflow-hidden mt-2">
        <thead className="bg-gray-900 text-white">
          <tr className="products-table-heading-tr">
            <th className="p-3 text-center">عکس</th>
            <th className="p-3 text-center">اسم</th>
            <th className="p-3 text-center">قیمت</th>
            <th className="p-3 text-center">موجودی</th>
            <th className="p-3 text-center">عملیات</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.id} className="border-b hover:bg-gray-100 text-center">
              <td className="p-3 flex justify-center">
                <img src={product.img} alt={product.title} className="w-16 h-16 object-cover rounded-md" />
              </td>
              <td className="p-3">{product.title}</td>
              <td className="p-3">{product.price.toLocaleString()} تومان</td>
              <td className="p-3">{product.count}</td>
              <td className="p-3 flex justify-center items-center space-x-2 rtl:space-x-reverse">
                <button onClick={() => handleEdit(product)} className="bg-yellow-500 text-white px-3 py-1 rounded-lg hover:bg-yellow-600 transition-all">
                  ویرایش
                </button>
                <button onClick={() => handleDelete(product.id)} className="bg-red-500 text-white px-3 py-1 rounded-lg hover:bg-red-600 transition-all">
                  حذف
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* فرم ویرایش */}
      {editProduct && (
        <div ref={formRef} className="mt-4 p-4 bg-gray-100 rounded-lg shadow">
          <h2 className="text-lg font-bold mb-3">ویرایش محصول</h2>
          <form onSubmit={handleUpdate} className="space-y-3">
            <input type="text" value={editProduct.title} onChange={(e) => setEditProduct({ ...editProduct, title: e.target.value })} className="w-full p-2 border rounded" placeholder="عنوان محصول" />
            <input type="number" value={editProduct.price} onChange={(e) => setEditProduct({ ...editProduct, price: e.target.value })} className="w-full p-2 border rounded" placeholder="قیمت محصول" />
            <input type="number" value={editProduct.count} onChange={(e) => setEditProduct({ ...editProduct, count: e.target.value })} className="w-full p-2 border rounded" placeholder="موجودی محصول" />
            <input type="text" value={editProduct.img} onChange={(e) => setEditProduct({ ...editProduct, img: e.target.value })} className="w-full p-2 border rounded" placeholder="آدرس تصویر محصول" />

            <div className="flex space-x-2 rtl:space-x-reverse">
              <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 transition-all">ذخیره</button>
              <button type="button" onClick={() => setEditProduct(null)} className="bg-gray-500 text-white px-4 py-2 rounded-lg hover:bg-gray-600 transition-all">لغو</button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
}
