import React, { useState, useEffect } from "react";
import axios from "axios";
import ErrorBox from "../Errorbox/ErrorBox";
import { FaTrash, FaUser } from "react-icons/fa";

export default function Users() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  // ÿØÿ±€åÿßŸÅÿ™ ⁄©ÿßÿ±ÿ®ÿ±ÿßŸÜ ÿßÿ≤ API
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get("http://localhost:3000/api/users");
        setUsers(response.data);
      } catch (error) {
        console.error("ÿÆÿ∑ÿß ÿØÿ± ÿØÿ±€åÿßŸÅÿ™ ⁄©ÿßÿ±ÿ®ÿ±ÿßŸÜ:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchUsers();
  }, []);

  // ÿ≠ÿ∞ŸÅ ⁄©ÿßÿ±ÿ®ÿ±
  const handleDeleteUser = async (userId) => {
    try {
      await axios.delete(`http://localhost:3000/api/users/${userId}`);
      setUsers(users.filter((user) => user.id !== userId));
    } catch (error) {
      console.error("ÿÆÿ∑ÿß ÿØÿ± ÿ≠ÿ∞ŸÅ ⁄©ÿßÿ±ÿ®ÿ±:", error);
    }
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold text-gray-900 text-center mb-6">üë• ŸÑ€åÿ≥ÿ™ ⁄©ÿßÿ±ÿ®ÿ±ÿßŸÜ</h2>

      {loading ? (
        <p className="text-center text-gray-500 animate-pulse">‚è≥ ÿØÿ± ÿ≠ÿßŸÑ ÿ®ÿßÿ±⁄Øÿ∞ÿßÿ±€å...</p>
      ) : users.length === 0 ? (
        <ErrorBox msg="Ÿá€å⁄Ü ⁄©ÿßÿ±ÿ®ÿ±€å ŸÖŸàÿ¨ŸàÿØ ŸÜ€åÿ≥ÿ™" />
      ) : (
        <ul className="space-y-4">
          {users.map((user) => (
            <li
              key={user.id}
              className="p-4 bg-white shadow-md border border-gray-200 rounded-lg flex justify-between items-center transition-all hover:shadow-lg"
            >
              <div className="flex items-center space-x-4">
                <FaUser className="text-gray-500 text-xl" />
                <div>
                  <p className="text-gray-800 font-semibold">{user.firstname} {user.lastname}</p>
                  <p className="text-gray-600 text-sm">{user.email}</p>
                </div>
              </div>
              <button
                onClick={() => handleDeleteUser(user.id)}
                className="bg-red-500 text-white py-1 px-3 rounded-lg hover:bg-red-700 transition-all flex items-center"
              >
                <FaTrash className="mr-1" /> ÿ≠ÿ∞ŸÅ
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
