import React, { useState, useEffect } from "react";
import axios from "axios";
import ErrorBox from "../Errorbox/ErrorBox";
import { FaTrash, FaUserEdit, FaUser } from "react-icons/fa";

export default function Users() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingUser, setEditingUser] = useState(null);
  const [updatedUser, setUpdatedUser] = useState({});

  // ÿØÿ±€åÿßŸÅÿ™ ⁄©ÿßÿ±ÿ®ÿ±ÿßŸÜ ÿßÿ≤ API
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get("http://localhost:3000/api/users");
        setUsers(response.data);
      } catch (error) {
        console.error("ÿÆÿ∑ÿß ÿØÿ± ÿØÿ±€åÿßŸÅÿ™ ⁄©ÿßÿ±ÿ®ÿ±ÿßŸÜ:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchUsers();
  }, []);

  // ÿ≠ÿ∞ŸÅ ⁄©ÿßÿ±ÿ®ÿ±
  const handleDeleteUser = async (userId) => {
    try {
      await axios.delete(`http://localhost:3000/api/users/${userId}`);
      setUsers(users.filter((user) => user.id !== userId));
    } catch (error) {
      console.error("ÿÆÿ∑ÿß ÿØÿ± ÿ≠ÿ∞ŸÅ ⁄©ÿßÿ±ÿ®ÿ±:", error);
    }
  };

  // ŸÜŸÖÿß€åÿ¥ ŸÖŸàÿØÿßŸÑ Ÿà€åÿ±ÿß€åÿ¥
  const handleEditUser = (user) => {
    setEditingUser(user);
    setUpdatedUser(user);
  };

  // ŸÖÿØ€åÿ±€åÿ™ ÿ™ÿ∫€å€åÿ±ÿßÿ™ ŸÅ€åŸÑÿØŸáÿß€å Ÿà€åÿ±ÿß€åÿ¥
  const handleChange = (e) => {
    setUpdatedUser({ ...updatedUser, [e.target.name]: e.target.value });
  };

  // ÿßÿ±ÿ≥ÿßŸÑ ÿ™ÿ∫€å€åÿ±ÿßÿ™ ÿ®Ÿá API
  const handleUpdateUser = async () => {
    try {
      const response = await axios.put(
        `http://localhost:3000/api/users/${updatedUser.id}`,
        updatedUser,
        {
          headers: { "Content-Type": "application/json" },
        }
      );

      if (response.status === 200) {
        // ÿ®Ÿá‚Äåÿ±Ÿàÿ≤ÿ±ÿ≥ÿßŸÜ€å ŸÑ€åÿ≥ÿ™ ⁄©ÿßÿ±ÿ®ÿ±ÿßŸÜ ÿØÿ± ŸÅÿ±ÿßŸÜÿ™‚ÄåÿßŸÜÿØ
        setUsers(users.map((user) => (user.id === updatedUser.id ? updatedUser : user)));
        setEditingUser(null);
      } else {
        console.error("ÿÆÿ∑ÿß€å ÿ∫€åÿ±ŸÖŸÜÿ™ÿ∏ÿ±Ÿá:", response.status);
      }
    } catch (error) {
      console.error("ÿÆÿ∑ÿß ÿØÿ± Ÿà€åÿ±ÿß€åÿ¥ ⁄©ÿßÿ±ÿ®ÿ±:", error.response ? error.response.data : error.message);
    }
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold text-gray-900 text-center mb-6">üë• ŸÑ€åÿ≥ÿ™ ⁄©ÿßÿ±ÿ®ÿ±ÿßŸÜ</h2>

      {loading ? (
        <p className="text-center text-gray-500 animate-pulse">‚è≥ ÿØÿ± ÿ≠ÿßŸÑ ÿ®ÿßÿ±⁄Øÿ∞ÿßÿ±€å...</p>
      ) : users.length === 0 ? (
        <ErrorBox msg="Ÿá€å⁄Ü ⁄©ÿßÿ±ÿ®ÿ±€å ŸÖŸàÿ¨ŸàÿØ ŸÜ€åÿ≥ÿ™" />
      ) : (
        <ul className="space-y-4">
          {users.map((user) => (
            <li
              key={user.id}
              className="p-4 bg-white shadow-md border border-gray-200 rounded-lg flex justify-between items-center transition-all hover:shadow-lg"
            >
              <div className="flex items-center space-x-4">
                <FaUser className="text-gray-500 text-xl" />
                <div>
                  <p className="text-gray-800 font-semibold">
                    {user.firstname} {user.lastname}
                  </p>
                  <p className="text-gray-600 text-sm">{user.email}</p>
                </div>
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => handleEditUser(user)}
                  className="bg-blue-500 text-white py-1 px-3 rounded-lg hover:bg-blue-700 transition-all flex items-center"
                >
                  <FaUserEdit className="mr-1" /> Ÿà€åÿ±ÿß€åÿ¥
                </button>
                <button
                  onClick={() => handleDeleteUser(user.id)}
                  className="bg-red-500 text-white py-1 px-3 rounded-lg hover:bg-red-700 transition-all flex items-center"
                >
                  <FaTrash className="mr-1" /> ÿ≠ÿ∞ŸÅ
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}

      {/* ŸÖŸàÿØÿßŸÑ Ÿà€åÿ±ÿß€åÿ¥ ⁄©ÿßÿ±ÿ®ÿ± */}
      {editingUser && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96">
            <h2 className="text-xl font-bold mb-4 text-center">Ÿà€åÿ±ÿß€åÿ¥ ⁄©ÿßÿ±ÿ®ÿ±</h2>
            <input
              type="text"
              name="firstname"
              value={updatedUser.firstname}
              onChange={handleChange}
              className="w-full p-2 border border-gray-300 rounded mb-2"
              placeholder="ŸÜÿßŸÖ ⁄©ÿßÿ±ÿ®ÿ±"
            />
            <input
              type="text"
              name="lastname"
              value={updatedUser.lastname}
              onChange={handleChange}
              className="w-full p-2 border border-gray-300 rounded mb-2"
              placeholder="ŸÜÿßŸÖ ÿÆÿßŸÜŸàÿßÿØ⁄Ø€å"
            />
            <input
              type="email"
              name="email"
              value={updatedUser.email}
              onChange={handleChange}
              className="w-full p-2 border border-gray-300 rounded mb-2"
              placeholder="ÿß€åŸÖ€åŸÑ"
            />
            <div className="flex justify-between mt-4">
              <button
                onClick={handleUpdateUser}
                className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-700"
              >
                ÿ∞ÿÆ€åÿ±Ÿá ÿ™ÿ∫€å€åÿ±ÿßÿ™
              </button>
              <button
                onClick={() => setEditingUser(null)}
                className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-700"
              >
                ŸÑÿ∫Ÿà
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
