import { Search, Bell } from "lucide-react";

const Header = () => {
  return (
    <header className="fixed top-0 left-0 right-60 bg-white shadow-md h-16 flex items-center px-6 justify-between">
      <h1 className="text-xl font-bold">داشبورد</h1>

      <div className="flex items-center space-x-4">
        <div className="relative">
          <input 
            type="text" 
            placeholder="جستجو..." 
            className="bg-gray-100 text-sm px-4 py-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-gray-300"
          />
          <Search className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-500" size={18} />
        </div>

        <Bell className="text-gray-600 cursor-pointer hover:text-gray-800 transition" size={24} />
      </div>
    </header>
  );
};

export default Header;
