import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");

    try {
      // شبیه‌سازی دریافت توکن ادمین (در صورتی که مسیر لاگین مشخص نباشد)
      const fakeToken = btoa(`${username}:${password}`); // تبدیل به Base64

      // بررسی اعتبار در مسیر /api/admins/me
      const response = await axios.get("http://localhost:3000/api/admins/me", {
        headers: { Authorization: `Bearer ${fakeToken}` },
      });

      console.log("✅ Login Success:", response.data);

      if (response.status === 200) {
        localStorage.setItem("adminToken", fakeToken);
        localStorage.setItem("adminData", JSON.stringify(response.data));

        onLogin(response.data);
        navigate("/");
      } else {
        setError("نام کاربری یا رمز عبور اشتباه است!");
      }
    } catch (err) {
      console.error("❌ Login Failed:", err.response?.data || err.message);
      setError("ورود ناموفق بود! لطفاً مجدداً تلاش کنید.");
    }
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      <form onSubmit={handleLogin} className="bg-white p-8 shadow-lg rounded-lg w-96">
        <h2 className="text-2xl font-bold text-center mb-4">ورود مدیر</h2>

        {error && <p className="text-red-500 text-center mb-4">{error}</p>}

        <div className="mb-4">
          <label className="block text-gray-700">نام کاربری</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded mt-1"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700">رمز عبور</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded mt-1"
            required
          />
        </div>

        <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700">
          ورود
        </button>
      </form>
    </div>
  );
};

export default Login;
