import React, { useState, useEffect } from "react";
import axios from "axios";
import ErrorBox from "../Errorbox/ErrorBox";
import { FaTrash } from "react-icons/fa"; // ÿ¢€å⁄©ŸàŸÜ ÿ≠ÿ∞ŸÅ

export default function Comments() {
  const [comments, setComments] = useState([]);
  const [loading, setLoading] = useState(true);

  // ÿØÿ±€åÿßŸÅÿ™ ⁄©ÿßŸÖŸÜÿ™‚ÄåŸáÿß ÿßÿ≤ API
  useEffect(() => {
    const fetchComments = async () => {
      try {
        const response = await axios.get("http://localhost:3000/api/comments");
        setComments(response.data);
      } catch (error) {
        console.error("ÿÆÿ∑ÿß ÿØÿ± ÿØÿ±€åÿßŸÅÿ™ ⁄©ÿßŸÖŸÜÿ™‚ÄåŸáÿß:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchComments();
  }, []);

  // ÿ™ÿßÿ®ÿπ ÿ≠ÿ∞ŸÅ ⁄©ÿßŸÖŸÜÿ™
  const handleDeleteComment = async (commentId) => {
    try {
      await axios.delete(`http://localhost:3000/api/comments/${commentId}`);
      setComments(comments.filter((comment) => comment.id !== commentId));
    } catch (error) {
      console.error("ÿÆÿ∑ÿß ÿØÿ± ÿ≠ÿ∞ŸÅ ⁄©ÿßŸÖŸÜÿ™:", error);
    }
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-2xl font-bold text-gray-900 text-center mb-6">
        üí¨ ŸÜÿ∏ÿ±ÿßÿ™ ⁄©ÿßÿ±ÿ®ÿ±ÿßŸÜ
      </h2>

      {loading ? (
        <p className="text-center text-gray-500 animate-pulse">‚è≥ ÿØÿ± ÿ≠ÿßŸÑ ÿ®ÿßÿ±⁄Øÿ∞ÿßÿ±€å...</p>
      ) : comments.length === 0 ? (
        <ErrorBox msg="Ÿá€å⁄Ü ⁄©ÿßŸÖŸÜÿ™€å ŸÖŸàÿ¨ŸàÿØ ŸÜ€åÿ≥ÿ™" />
      ) : (
        <ul className="space-y-4">
          {comments.map((comment) => (
            <li
              key={comment.id}
              className="p-4 bg-white shadow-md border border-gray-200 rounded-lg flex justify-between items-center transition-all hover:shadow-lg"
            >
              <p className="text-gray-700">{comment.body}</p>
              <button
                onClick={() => handleDeleteComment(comment.id)}
                className="bg-red-500 text-white p-2 rounded-lg hover:bg-red-700 transition-all flex items-center"
              >
                <FaTrash className="mr-1" /> ÿ≠ÿ∞ŸÅ
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
