import React, { useState } from "react";
import axios from "axios";

export default function AddNewProduct({ onProductAdded }) {
  const [newProduct, setNewProduct] = useState({
    title: "",
    price: "",
    count: "",
    img: "",
    popularity: "",
    sale: "",
    colors: "",
  });

  const handleChange = (e) => {
    setNewProduct({ ...newProduct, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:3000/api/products", newProduct);
      if (response.status === 201) {
        onProductAdded(response.data); // اضافه کردن محصول به لیست نمایش
        setNewProduct({
          title: "",
          price: "",
          count: "",
          img: "",
          popularity: "",
          sale: "",
          colors: "",
        });
      }
    } catch (error) {
      console.error("خطا در افزودن محصول:", error);
    }
  };

  return (
    <div className="products-main bg-white shadow-lg rounded-2xl p-6 w-full max-w-lg mx-auto mt-6">
      <h1 className="product-title text-xl font-semibold text-gray-800 text-center mb-4">افزودن محصول جدید</h1>
      <form onSubmit={handleSubmit} className="add-products-form space-y-4">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {[
            { name: "title", placeholder: "اسم محصول" },
            { name: "price", placeholder: "قیمت محصول" },
            { name: "count", placeholder: "موجودی محصول" },
            { name: "img", placeholder: "آدرس عکس محصول" },
            { name: "popularity", placeholder: "میزان محبوبیت محصول" },
            { name: "sale", placeholder: "میزان فروش محصول" },
            { name: "colors", placeholder: "تعداد رنگ محصول", fullWidth: true },
          ].map(({ name, placeholder, fullWidth }) => (
            <div key={name} className={`add-products-form-wrap ${fullWidth ? "md:col-span-2" : ""}`}>
              <input
                type="text"
                name={name}
                value={newProduct[name]}
                onChange={handleChange}
                placeholder={`${placeholder} را وارد کنید`}
                className="add-products-input w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
          ))}
        </div>
        <button type="submit" className="w-full bg-gray-900 text-white py-2 rounded-lg hover:bg-blue-700 transition-all">
          افزودن محصول
        </button>
      </form>
    </div>
  );
}
