import { BrowserRouter as Router, Routes, Route, Navigate, useRoutes } from "react-router-dom";
import { useEffect, useState } from "react";
import Sidebar from "./Components/SideBar/Sidebar";
import Header from "./Components/Header/Header";
import Login from "./Components/مogin/Login";
import routes from "./routes";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [adminData, setAdminData] = useState(null);
  const router = useRoutes(routes);

  useEffect(() => {
    const token = localStorage.getItem("adminToken");
    const adminInfo = localStorage.getItem("adminData");

    if (token && adminInfo) {
      setIsAuthenticated(true);
      setAdminData(JSON.parse(adminInfo));
    }
  }, []);

  return (
    <Router>
      <Routes>
        {/* صفحه لاگین */}
        <Route path="/login" element={<Login onLogin={(admin) => {
          setIsAuthenticated(true);
          setAdminData(admin);
        }} />} />

        {/* پنل مدیریت (فقط اگر لاگین باشد) */}
        <Route
          path="/*"
          element={isAuthenticated ? (
            <div className="flex h-screen">
              {/* سایدبار */}
              <Sidebar />

              {/* محتوای اصلی */}
              <div className="flex flex-col flex-1">
                {/* هدر */}
                <Header adminData={adminData} />

                {/* محتوای صفحه */}
                <main className="flex-1 p-6 bg-gray-100 overflow-auto">
                  {router}
                </main>
              </div>
            </div>
          ) : (
            <Navigate to="/login" />
          )}
        />
      </Routes>
    </Router>
  );
}

export default App;
